msg.topic ='karien'
msg.payload = msg.topic + ' travel time is ' + msg.traveltime;
msg.mqtttopictext = 'tele/travel/' + msg.topic
msg.led = 'green'
msg.mqtttopicled = 'tele/travel/' + msg.topic + '/led/'
