if (msg.payload._type === "location")
{
//    var date = new Date(parseInt(msg.payload.tst) * 1000);

    msg.payload.data = {
        "battery" : parseInt(msg.payload.batt),
        "dev_id" : msg.topic.replace(/\/|-/g, "_").toLowerCase(),
        "gps" : [parseFloat(msg.payload.lat),parseFloat(msg.payload.lon)],
        "gps_accuracy" : msg.payload.acc
    };
    msg.timestamp = msg.payload.tst;
}
else
    msg = null;

return msg;

distance = parseInt(msg.location.distances.Home);
interval = parseInt(0.000000000000475 * Math.pow(distance,4) - 0.00000000475 * Math.pow(distance,3) + 0.0000265 * Math.pow(distance,2) + 0.0037 * distance + 1.43);
dev_id = msg.topic.replace(/\/|-/g, "_").toLowerCase();

if (interval < 3) interval = 3;
if (interval > 300) interval = 300;

lastInterval = flow.get(dev_id + '_last_owntracks_interval');
lastTimestamp = flow.get(dev_id + '_last_owntracks_timestamp');

var temp = new Date(parseInt(msg.timestamp) * 1000);
node.status({fill: "green", shape: "dot", text: interval + " at " + temp.toLocaleString()});

if (typeof(lastInterval) != "undefined")
{
	if ((msg.timestamp - 3600) < lastTimestamp) // less than 1 hour since last update?
	{
		if (lastInterval > (interval / 1.05) && lastInterval < (interval * 1.05)) // within 5% of last update?
        {
            node.status({fill: "red", shape: "ring", text: interval + " at " + temp.toLocaleString()});
            msg = null;
        }
        else
		{
            node.status({fill: "green", shape: "dot", text: msg.timestamp + " versus " + lastTimestamp});
            flow.set('_last_owntracks_interval', interval);
            flow.set('_last_owntracks_timestamp', msg.timestamp);

            msg.topic = msg.topic + "/cmd";
            msg.payload = {
                "_type" : "cmd",
                "action" : "setConfiguration",
                "configuration" : {
                    "_type" : "configuration",
                    "beaconMode" : 2,
                    "locatorDisplacement" : interval * 2,
                    "locatorInterval" : interval,
                    "pub" : "true",
                    "pubQos" : 2
                }
            };
        }
    }
    else
    {
        flow.set(dev_id + '_last_owntracks_interval', interval);
        flow.set(dev_id + '_last_owntracks_timestamp', msg.timestamp);

        msg.topic = msg.topic + "/cmd";
        msg.payload = {
            "_type" : "cmd",
            "action" : "setConfiguration",
            "configuration" : {
                "_type" : "configuration",
                "beaconMode" : 2,
                "locatorDisplacement" : interval * 2,
                "locatorInterval" : interval,
                "pub" : "true",
                "pubQos" : 2
            }
        };
    }
}
else
{
    flow.set(dev_id + '_last_owntracks_interval', interval);
    flow.set(dev_id + '_last_owntracks_timestamp', msg.timestamp);

    msg.topic = msg.topic + "/cmd";
    msg.payload = {
        "_type" : "cmd",
        "action" : "setConfiguration",
        "configuration" : {
            "_type" : "configuration",
            "beaconMode" : 2,
            "locatorDisplacement" : interval * 2,
            "locatorInterval" : interval,
            "pub" : "true",
            "pubQos" : 2
        }
    };
}

return msg;

